<?phpnamespace app\common\model;class QuestionOption extends BaseModel{    protected $table = 'question_option';    static function addSome(int $question_id , array $bodies)    {        if(count($bodies) == 0 )            exception('选项不能为空');        static::where(compact('question_id'))->delete();        $insert = [];        foreach ($bodies as $k => $body) {            $insert[] = [                'body' => $body,                'question_id' => $question_id,                'sort_num' => chr($k + 65),            ];        }        return static::insertAll($insert);    }}