mapper.select:
select * from ( select count(0) lesson_count from lesson where id in (select DISTINCT lesson_id from lesson_teacher where teacher_id = #{teacherId}) and DATE_FORMAT( add_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' ) ) g1, (
select count(0) rollcall_count from lesson_student where teacher_id = #{teacherId} and DATE_FORMAT( sign_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
) g2, (
select count(0) course_count from student_course where operator = #{teacherId} and deleted = 0 and DATE_FORMAT( add_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
) g3, (
select count(0) student_count from student where creator = #{teacherId} and deleted = 0 and DATE_FORMAT( add_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' ) ) g4
