mapper.select:
select t1.id,t1.refund_sn,t1.refund_money,t1.item_names,t1.verify_time,t1.verify_remark, t1.wx_refund_id,t1.add_time,t1.reason,t1.state, t2.name studentName, t3.sn order_sn, t4.name verify_name, t5.name reason_type_text from shop_order_refund t1 left join shop_order t3 on t3.id = t1.order_id left join student t2 on t2.id = t1.student_id left join staff t4 on t4.id = t1.verify_id left join dict_item t5 on t5.id = t1.reason_type
AND t1.order_id = #{param.orderId}
AND t1.student_id = #{param.studentId}
AND t3.sn like concat( #{param.orderSn}, '%')
AND t1.state in
#{id}
AND t1.refund_sn like concat( #{param.refundSn}, '%')
AND t5.id = #{param.reasonType}
AND ( t1.add_time >= #{param.startDate} and t1.add_time <= #{param.endDate} )
order by t1.id desc
